function check(){for(var e=document.getElementById("checkbox"),l=document.getElementsByClassName("monthly"),c=document.getElementsByClassName("yearly"),t=0;t<l.length;t++)0==e.checked?(l[t].style.display="block",c[t].style.display="none"):1==e.checked&&(l[t].style.display="none",c[t].style.display="block")}check();